import TinymceEditor from "../addRegulations/TinymceEditor";
export default {
  components: {
    TinymceEditor: TinymceEditor
  },
  data: function data() {
    return {
      disabled: false,
      regulationsList: [],
      valueId: '',
      // 联动选择框的id主键
      isResouceShow: 0,
      ruleForm: {
        regulationsId: '',
        clauseNo: '',
        clauseNoAlias: '',
        clauseVersion: '',
        clauseTitle: '',
        clauseContent: '',
        clauseAntistop: '',
        directoryForeignKey: '',
        clauseId: ''
      },
      rules: {
        regulationsId: [{
          required: true,
          message: '请选择法规',
          trigger: 'change'
        }],
        directoryForeignKey: [{
          required: true,
          message: '请选择法规目录',
          trigger: 'change'
        }],
        clauseNo: [{
          required: true,
          message: '请输入条款编号',
          trigger: 'blur'
        }, {
          type: 'number',
          message: '请输入数字',
          trigger: 'blur'
        }],
        clauseNoAlias: [{
          required: true,
          message: '请输入条款编号别名',
          trigger: 'blur'
        }],
        clauseVersion: [{
          required: true,
          message: '请输入条款版本号',
          trigger: 'blur'
        }],
        clauseTitle: [{
          required: true,
          message: '请输入条款标题',
          trigger: 'blur'
        }],
        clauseContent: [{
          required: true,
          message: '请输入条款内容'
        }],
        clauseAntistop: [{
          required: true,
          message: '请输入关键词',
          trigger: 'blur'
        }]
      },
      options: []
    };
  },
  mounted: function mounted() {
    // 初始化一级分类下拉
    this.getRegulations();
    this.init();
  },
  methods: {
    getRegulations: function getRegulations() {
      var _this2 = this;

      this.$http.post(this.$url + 'regulations/showAll').then(function (res) {
        if (res.data.code == '2001') {
          _this2.regulationsList = res.data.data;
        } else {
          _this2.regulationsList = [];
        }
      });
    },
    init: function init() {
      var _this3 = this;

      this.clauseId = this.$route.query.clauseId; // 获取条款的主键

      var params = new URLSearchParams();
      params.append('clauseId', this.clauseId);
      this.$http.post(this.$url + 'clause/queryClauseByClauseId', params).then(function (res) {
        if (res.data.code == '2001') {
          _this3.ruleForm = res.data.data; // 把数据放入页面

          console.log(JSON.stringify(_this3.ruleForm));
          _this3.ruleForm.regulationsId = res.data.data.regulationsForeignkey; //视图的参数名和回传的数据不一致，手动赋值

          var _params = new URLSearchParams();

          _params.append('directoryId', _this3.ruleForm.regulationsId); //获取当前的法规主键


          _this3.$http.post(_this3.$url + 'directory/selectDirectoryById2', _params).then(function (res) {
            if (res.data.code == '2001') {
              _this3.options = res.data.data; //渲染目录下拉框

              _this3.valueId = _this3.ruleForm.directoryForeignKey; //视图默认选中
            } else {
              _this3.options = [];
            }
          });
        }
      });
    },
    onChange: function onChange() {
      var _this4 = this;

      ++this.isResouceShow;
      this.valueId = '';
      this.options = [];
      var params = new URLSearchParams();
      params.append('directoryId', this.ruleForm.regulationsId);
      this.$http.post(this.$url + 'directory/selectDirectoryById2', params).then(function (res) {
        if (res.data.code == '2001') {
          _this4.options = res.data.data;
        } else {
          _this4.options = [];
        }
      });
    },
    onHandleChange: function onHandleChange() {
      console.log(JSON.stringify(this.valueId));

      if (this.valueId.length === 1) {
        this.ruleForm.directoryForeignKey = this.valueId[0];
      } else {
        this.ruleForm.directoryForeignKey = this.valueId[this.valueId.length - 1];
      }
    },
    // 表单的提交
    submitForm: function submitForm(formName) {
      var _this5 = this;

      this.$refs[formName].validate(function (valid) {
        if (valid) {
          var _this = _this5;
          console.log(JSON.stringify(_this.ruleForm));

          _this5.$http.post(_this5.$url + 'clause/updateClause', _this.ruleForm).then(function (res) {
            if (res.data.code == '2001') {
              _this5.$message({
                message: '修改成功',
                type: 'success'
              });
            } else {
              _this5.$message.error('修改失败');
            }
          });
        } else {
          console.log('error submit!!');
          return false;
        }
      });
    },
    // 清空表单以及富文本编译器内容
    resetForm: function resetForm(formName) {
      this.$refs[formName].resetFields();
      this.$refs.editor.clear();
      this.options = [];
    },
    // 文本编译器点击事件
    onClick: function onClick(e, editor) {
      console.log('Element clicked');
      console.log(e);
      console.log(editor);
    },
    // 获取文本编译器的HTML内容
    getContent: function getContent() {
      alert(this.$refs.editor.getContent());
    },
    // 设置文本编译器的HTML内容
    setContent: function setContent(msg) {
      this.$refs.editor.setContent(msg);
    },
    getBack: function getBack() {
      this.$router.push({
        path: '/example/clauseList'
      });
    }
  }
};
