import tinymce from 'tinymce/tinymce';
import Editor from '@tinymce/tinymce-vue';
import 'tinymce/themes/silver'; // 编辑器插件plugins
// 更多插件参考：https://www.tiny.cloud/docs/plugins/

import 'tinymce/plugins/image'; // 插入上传图片插件

import 'tinymce/plugins/media'; // 插入视频插件

import 'tinymce/plugins/table'; // 插入表格插件

import 'tinymce/plugins/lists'; // 列表插件

import 'tinymce/plugins/wordcount'; // 字数统计插件

import 'tinymce/plugins/fullscreen'; //全屏

import 'tinymce/plugins/insertdatetime'; //时间

import 'tinymce/plugins/paste'; //支持图片复制拖拽

import '@dimakorotkov/tinymce-mathjax'; //支持数学公式

export default {
  components: {
    Editor: Editor
  },
  props: {
    value: {
      type: String,
      default: ''
    },
    // 基本路径，默认为空根目录，如果你的项目发布后的地址为目录形式，
    // 即abc.com/tinymce，baseUrl需要配置成tinymce，不然发布后资源会找不到
    baseUrl: {
      type: String,
      default: ''
    },
    disabled: {
      type: Boolean,
      default: false
    },
    plugins: {
      type: [String, Array],
      default: 'lists image media table wordcount fullscreen insertdatetime paste mathjax'
    },
    toolbar: {
      type: [String, Array],
      default: 'undo redo |  formatselect | bold italic forecolor backcolor | alignleft aligncenter alignright alignjustify | bullist numlist outdent indent | lists image media table | removeformat | fullscreen | insertdatetime | paste | mathjax'
    }
  },
  data: function data() {
    return {
      init: {
        language_url: "tinymce/langs/zh_CN.js",
        language: 'zh_CN',
        skin_url: "tinymce/skins/ui/oxide",
        content_css: "tinymce/skins/content/default/content.css",
        // skin_url: `${this.baseUrl}/tinymce/skins/ui/oxide-dark`, // 暗色系
        // content_css: `${this.baseUrl}/tinymce/skins/content/dark/content.css`, // 暗色系
        paste_data_images: true,
        // 设置为“true”将允许粘贴图像，而将其设置为“false”将不允许粘贴图像。
        height: 300,
        plugins: this.plugins,
        toolbar: this.toolbar,
        branding: false,
        menubar: false,
        mathjax: {
          lib: 'mathjax/es5/tex-mml-chtml.js'
        },
        // 此处为图片上传处理函数，这个直接用了base64的图片形式上传图片，
        // 如需ajax上传可参考https://www.tiny.cloud/docs/configure/file-image-upload/#images_upload_handler
        images_upload_handler: function images_upload_handler(blobInfo, success, failure) {
          var img = 'data:image/jpeg;base64,' + blobInfo.base64();
          success(img);
        }
      },
      myValue: this.value
    };
  },
  watch: {
    value: function value(newValue) {
      this.myValue = newValue;
    },
    myValue: function myValue(newValue) {
      this.$emit('input', newValue);
    }
  },
  mounted: function mounted() {
    tinymce.init({});
  },
  methods: {
    // 添加相关的事件，可用的事件参照文档=> https://github.com/tinymce/tinymce-vue => All available events
    // 需要什么事件可以自己增加
    onClick: function onClick(e) {
      this.$emit('onClick', e, tinymce);
    },
    // 可以添加一些自己的自定义事件，如清空内容
    clear: function clear() {
      this.myValue = '';
    },
    getContent: function getContent() {
      return tinyMCE.activeEditor.getContent();
    },
    setContent: function setContent(msg) {
      return tinyMCE.activeEditor.setContent(msg);
    }
  }
};
