export default {

  data() {
    return {
      ruleForm: {
        webId:'',
        webStatus: '',
        webTitle: '',
        webDomainName: '',
        webEmail: '',
        language: '',
        webLogoPath: ''
      },
      rules: {
        webTitle: [
          { required: true, message: '请输入网站标题', trigger: 'blur' },
          { min: 3, max: 100, message: '长度在 3 到 100 个字符', trigger: 'blur' }
        ],
        language: [
          { required: true, message: '请选择语音', trigger: 'change' }
        ],
        webDomainName: [
          { required: true, message: '请输入域名', trigger: 'blur' },
          { min: 3, max: 100, message: '长度在 3 到 100 个字符', trigger: 'blur' }
        ],
        webEmail: [
          { required: true, message: '请输入邮箱地址', trigger: 'blur' },
          { type: 'email', message: '请输入正确的邮箱地址', trigger: ['blur', 'change'] }
        ],
        webStatus: [
          { required: true, message: '请选择网站动态', trigger: 'change' }
        ]
      }
    }
  },
  mounted() {
    this.init()
  },
  methods: {
    // 移除图片清空图片的base64
    handleRemove(file, fileList) {
      console.log(file, fileList)
      this.ruleForm.webLogoPath = ''
    },
    // 触发事件
    handleChange(file, fileList) {
      this.getBase64(file.raw).then(res => {
        this.ruleForm.webLogoPath = res
      })
    },

    init() {
      this.$http.post(this.$url + 'admin/initWeb',).then((res) => {
        if (res.data.code == '2001') {
          this.ruleForm = res.data.data
        } else {
          this.ruleForm = []
        }
      })
    },
    // 图片转换base64
    getBase64(file) {
      return new Promise(function(resolve, reject) {
        const reader = new FileReader()
        let imgResult = ''
        reader.readAsDataURL(file)
        reader.onload = function() {
          imgResult = reader.result
        }
        reader.onerror = function(error) {
          reject(error)
        }
        reader.onloadend = function() {
          resolve(imgResult)
        }
      })
    },
    // 上传法规图标的方法
    beforeAvatarUpload(file) {
      const isJPG = file.type === 'image/jpeg'
      const isLt2M = file.size / 1024 / 1024 < 2

      if (!isJPG) {
        this.$message.error('上传头像图片只能是 JPG 格式!')
      }
      if (!isLt2M) {
        this.$message.error('上传头像图片大小不能超过 2MB!')
      }
      return isJPG && isLt2M
    },
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          console.log(JSON.stringify(this.ruleForm))
          this.$http.post(this.$url + 'admin/updateWeb',this.ruleForm).then((res) => {
            if (res.data.code == '2001') {
              this.$message({
                message: '修改成功',
                type: 'success'
              })
            } else {
              this.$message.error('修改失败')
            }
          })
        } else {
          console.log('error submit!!')
          return false
        }
      })
    },
    resetForm(formName) {
      this.$refs[formName].resetFields()
    }
  }
}
