export default {
  data() {
    var isMobileNumber = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('请输入密码'))
      } else {
        const reg = /^1[3|4|5|7|8][0-9]\d{8}$/
        const isPhone = reg.test(value)
        value = Number(value) // 转换为数字
        if (typeof value === 'number' && !isNaN(value)) { // 判断是否为数字
          value = value.toString() // 转换成字符串
          if (value.length < 0 || value.length > 12 || !isPhone) { // 判断是否为11位手机号
            callback(new Error('手机号码格式如:138xxxx8754'))
          } else {
            callback()
          }
        } else {
          callback(new Error('请输入电话号码'))
        }
      }
    }

    return {
      loading: true,
      formVisible: false,
      dialogFormVisible: false,
      ruleForm: {},
      form: {},
      search: '',
      currentPage: 1,
      pageSize: 10,
      total: 0,
      tableData: [],
      rules: {
        adminName: [
          { required: true, message: '请输入管理员姓名', trigger: 'blur' },
          { max: 100, message: '长度不超过100个字符', trigger: 'blur' }
        ],
        adminPhone: [
          { validator: isMobileNumber, trigger: 'blur' }
        ],
        adminPassword: [
          { required: true, message: '请输入密码', trigger: 'blur' },
          { min: 6, max: 20, message: '长度在 6 到 20 个字符', trigger: 'blur' }
        ]
      },
      adminRules: {
        adminName: [
          { required: true, message: '请输入管理员姓名', trigger: 'blur' },
          { max: 100, message: '长度不超过100个字符', trigger: 'blur' }
        ],
        adminPassword: [
          { required: true, message: '请输入密码', trigger: 'blur' },
          { min: 6, max: 20, message: '长度在 6 到 20 个字符', trigger: 'blur' }
        ]
      }
    }
  },
  mounted() {
    this.onload()
  },
  methods: {
    close() {
      this.onload()
    },
    onAdd() {
      this.dialogFormVisible = true
    },
    onSubmit(ruleForm) {
      this.$refs[ruleForm].validate((valid) => {
        if (valid) {
          this.$http.post(this.$url + 'admin/addAdmin', this.ruleForm).then((res) => {
            this.loading = true
            if (res.data.code === 2001) {
              this.$message({
                message: '新增成功',
                type: 'success'
              })
            } else if (res.data.code === 2002) {
              this.$message.error('手机号重复')
            } else {
              this.$message.error('添加失败')
            }
            this.dialogFormVisible = false
            this.$refs[ruleForm].resetFields();
            this.onload()
          })
        } else {
          console.log('error submit!!')
          // this.dialogFormVisible = false
          // this.loading = true
          // return false
        }
      })
    },
    quxiao(ruleForm){
      this.dialogFormVisible = false
      // this.ruleForm.adminName=''
      // this.ruleForm.adminPhone=''
      // this.ruleForm.adminPassword=''
      this.$refs[ruleForm].resetFields();
    },
    onChange(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          this.$http.post(this.$url + 'admin/updateAdmin', this.form).then((res) => {
            this.loading = true
            if (res.data.code === 2001) {
              this.$message({
                message: '修改成功',
                type: 'success'
              })
              this.formVisible = false
              this.onload()
            } else {
              this.$message.error('修改失败')
            }
          })
        } else {
          console.log('error submit!!')
          return false
        }
      })
    },
    handleEdit(index, row) {
      this.form = row
      this.formVisible = true
    },
    handleDelete(index, row) {
      this.$confirm('此操作将永久删除该数据, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        const params = new URLSearchParams()
        params.append('adminId', row.adminId)
        this.$http.post(this.$url + 'admin/delAdmin', params).then((res) => {
          if (res.data.code === 2001) {
            this.$message({
              message: '删除成功',
              type: 'success'
            })
            this.onload()
          }
          if (res.data.code === 2004) {
            this.$message.error('删除失败')
          }
        })
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '已取消删除'
        })
      })
    },
    handleSizeChange: function(size) {
      this.pageSize = size
      this.onload()
    },
    handleCurrentChange: function(currentPage) {
      this.currentPage = currentPage
      this.onload()
    },
    onload() {
      const params = new URLSearchParams()
      params.append('page', this.currentPage)
      params.append('limit', this.pageSize)
      this.$http.post(this.$url + 'admin/showAdmin', params).then((res) => {
        this.loading = true
        if (res.data.code === 2001) {
          // console.log('请求成功')
          this.tableData = res.data.data
          this.total = res.data.count
          this.loading = false
        }
        if (res.data.code === 3001) {
          // console.log('请求失败')
          this.loading = false
        }
      })
    }
  }

}
