export default {
  data() {
    return {
      ruleForm: {
        smtpId: '',
        smtpEmail: '',
        smtpServer: '',
        smtpUsername: '',
        smtpPassword: '',
        number: '',
        loginType: ''
      },
      rules: {
        smtpServer: [
          { required: true, message: '请输入服务器', trigger: 'blur' },
          { min: 3, max: 5, message: '长度在 3 到 5 个字符', trigger: 'blur' }
        ],
        smtpUsername: [
          { required: true, message: '请输入账户', trigger: 'blur' },
          { min: 3, max: 5, message: '长度在 3 到 5 个字符', trigger: 'blur' }
        ],
        smtpPassword: [
          { required: true, message: '请输入密码', trigger: 'blur' },
          { min: 3, max: 5, message: '长度在 3 到 5 个字符', trigger: 'blur' }
        ],
        smtpEmail: [
          { required: true, message: '请输入邮箱地址', trigger: 'blur' },
          { type: 'email', message: '请输入正确的邮箱地址', trigger: ['blur', 'change'] }
        ],
        loginType: [
          { required: true, message: '请选择登录类型', trigger: 'change' }
        ],
        number: [
          { required: true, message: '请填写次数', trigger: 'blur' }
        ]
      }
    }
  },
  mounted() {
    this.init()
  },
  methods: {
    init() {
      this.$http.post(this.$url + 'admin/initSmtp',).then((res) => {
          this.ruleForm = res.data
      })
    },
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          // alert('submit!')
          this.$http.post(this.$url + 'admin/updateSmtp',this.ruleForm).then((res) => {
            if (res.data.code == '2001') {
              this.$message({
                message: '修改成功',
                type: 'success'
              })
            } else {
              this.$message.error('修改失败')
            }
          })
        } else {
          console.log('error submit!!')
          return false
        }
      })
    },
    resetForm(formName) {
      this.$refs[formName].resetFields()
    }
  }
}
