export default {
  name: 'RegulationsList',
  data() {
    return {
      tableData: [],
      search: '',
      currentPage: 1,
      pageSize: 10,
      total: 0,
      loading: true
    }
  },
  mounted() {
    this.getData()
  },
  methods: {
    btn() {
      this.$router.push({ path: 'addRegulations' })// 路由跳转页面
    },
    handleEdit(index, row) {
      this.$router.push({
        name: 'UpdateRegulations',
        query: {
          regulationsId: row.regulationsId
        }
      })
    },
    handleDelete(index, row) {
      this.$http.get(this.$url + 'regulations/delRegulations', {
        params: { 'regulationsId': row.regulationsId }
      }).then((res) => {
        if (res.data.code == '2001') {
          this.$message({
            message: '删除成功',
            type: 'success'
          })
          this.getData()
        } else {
          this.$message.error('删除失败')
        }
      })
    },
    handleSizeChange(size) {
      this.pageSize = size
      this.getData()
    },
    handleCurrentChange(currentPage) {
      this.currentPage = currentPage
      this.getData()
    },
    getData() {
      const params = new URLSearchParams()
      params.append('page', this.currentPage)
      params.append('limit', this.pageSize)
      this.$http.post(this.$url + 'regulations/showRegulations', params).then((res) => {
        if (res.data.code == '2001') {
          this.loading = false
          this.tableData = res.data.data
          this.total = res.data.count
        } else {
          this.total = res.data.count
          this.loading = false
          this.tableData = []
        }
      })
    }
  }
}
