export default {
  name: 'Dashboard',
  // computed: {
  //   ...mapGetters([
  //     'name'
  //   ])
  // }
  data(){
    return{
      time:'',
      timer:''
    }
  },
  created () {
    this.timer=setInterval(() => {
          this.init()
      }, 1000)
  },
  methods: {
    init() {
      this.$http.post(this.$url + 'admin/time').then((res) => {
        this.time=res.data
      })
    }
  },
  beforeDestroy() {

    if (this.timer) {
      alert('1')
      clearInterval(this.timer); // 在Vue实例销毁前，清除我们的定时器
    }
  }
}
